SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_repasse_lib_conta ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


qt_material_w			bigint;
qt_procedimento_w		bigint;
ds_retorno_w			varchar(10);


BEGIN
ds_retorno_w	:= 'N';
select	count(*)
into STRICT	qt_procedimento_w
from	procedimento_paciente a,
	procedimento_repasse b
where	a.nr_sequencia		= b.nr_seq_procedimento
and	a.nr_interno_conta	= nr_interno_conta_p
and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');

select	count(*)
into STRICT	qt_material_w
from	material_atend_paciente a,
	material_repasse b
where	a.nr_sequencia		= b.nr_seq_material
and	a.nr_interno_conta	= nr_interno_conta_p
and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');

if	((qt_material_w	+ qt_procedimento_w )	> 0) then
	ds_retorno_w		:= 'S';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_repasse_lib_conta ( nr_interno_conta_p bigint) FROM PUBLIC;

