CREATE OR REPLACE FUNCTION obter_se_repasse_unificado ( nr_seq_proc_repasse_p bigint, nr_seq_mat_repasse_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1);
nr_repasse_terceiro_w	repasse_terceiro.nr_repasse_terceiro%type;


BEGIN

ds_retorno_w	:= 'S';

if (nr_seq_proc_repasse_p IS NOT NULL AND nr_seq_proc_repasse_p::text <> '') then
	select	max(a.nr_repasse_terceiro)
	into STRICT	nr_repasse_terceiro_w
	from	procedimento_repasse a
	where	a.nr_sequencia	= nr_seq_proc_repasse_p;

	if (nr_repasse_terceiro_w IS NOT NULL AND nr_repasse_terceiro_w::text <> '') then
		select	'N'
		into STRICT	ds_retorno_w
		from	repasse_terceiro a
		where	a.nr_repasse_terceiro	= nr_repasse_terceiro_w
		and	a.ie_status	= 'U';
	end if;
elsif (nr_seq_mat_repasse_p IS NOT NULL AND nr_seq_mat_repasse_p::text <> '') then
	select	max(a.nr_repasse_terceiro)
	into STRICT	nr_repasse_terceiro_w
	from	material_repasse a
	where	a.nr_sequencia	= nr_seq_proc_repasse_p;

	if (nr_repasse_terceiro_w IS NOT NULL AND nr_repasse_terceiro_w::text <> '') then
		select	'N'
		into STRICT	ds_retorno_w
		from	repasse_terceiro a
		where	a.nr_repasse_terceiro	= nr_repasse_terceiro_w
		and	a.ie_status	= 'U';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
