CREATE OR REPLACE FUNCTION obter_se_repres_grupo_prot (cd_protocolo_p bigint, nr_seq_grupo_p bigint, nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(2) := 'N';
qt_representante_w	bigint := 0;
nr_seq_grupo_w		bigint;


BEGIN
if (coalesce(cd_protocolo_p,0) > 0) and (coalesce(nr_seq_grupo_p,0) > 0)  and (coalesce(nr_seq_protocolo_p,0) > 0) then

	select count(*)
	into STRICT    qt_representante_w
	from 	protocolo_medic_material
	where	cd_protocolo		= cd_protocolo_p
	and	nr_sequencia		= nr_seq_protocolo_p
	and	nr_seq_grupo		= nr_seq_grupo_p
	and 	coalesce(ie_princ_grupo_rotina,'N') = 'S';

	if (qt_representante_w = 1) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
