CREATE OR REPLACE FUNCTION obter_se_requisicao_consignada ( nr_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(01);
ie_consignado_w		varchar(01);
cd_operacao_estoque_w	smallint;


BEGIN

ie_retorno_w	:= 'N';

select	max(cd_operacao_estoque)
into STRICT	cd_operacao_estoque_w
from	requisicao_material
where	nr_requisicao = nr_requisicao_p;

select	coalesce(max(ie_consignado),'0')
into STRICT	ie_consignado_w
from	operacao_estoque b
where	cd_operacao_estoque	= cd_operacao_estoque_w;


if (ie_consignado_w <> '0') then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
