CREATE OR REPLACE FUNCTION obter_se_requisicao_pendente ( nr_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_pendente_w	integer;
qt_itens_w	integer;
ie_retorno_w	varchar(1);
	/*
	A = Atendida
	P = Parcialmente atendida
	N = NÃ£o atendida
	*/
BEGIN

ie_retorno_w	:= 'N';


select	count(*)
into STRICT	qt_itens_w
from	item_requisicao_material
where	nr_requisicao = nr_requisicao_p;

select	count(*)
into STRICT	qt_pendente_w
from	item_requisicao_material
where	obter_tipo_motivo_baixa_req(cd_motivo_baixa)		= 0
and	nr_requisicao		= nr_requisicao_p;

if (qt_pendente_w = 0) then
	ie_retorno_w	:= 'A';
elsif (qt_pendente_w > 0) and (qt_pendente_w < qt_itens_w) then
	ie_retorno_w	:= 'P';
elsif (qt_pendente_w > 0) and (qt_pendente_w = qt_itens_w) then
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
