CREATE OR REPLACE FUNCTION obter_se_reserva_cancelada (nr_seq_transporte_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1):= 'N';
qt_reg_dependentes_w	bigint;
nr_seq_reserva_w	bigint;


BEGIN

qt_reg_dependentes_w:= 0;

select	count(*)
into STRICT	qt_reg_dependentes_w
from 	via_reserva_transporte	a,
	via_transporte		b,
	via_reserva		c
where 	a.nr_seq_transporte = b.nr_sequencia
and 	a.nr_seq_reserva    = c.nr_sequencia
and 	a.nr_seq_transporte = nr_seq_transporte_p
and 	(c.ie_cancelamento IS NOT NULL AND c.ie_cancelamento::text <> '');


if (qt_reg_dependentes_w > 0) then
	ie_retorno_w:= 'S';
else
	ie_retorno_w:= 'N';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
