CREATE OR REPLACE FUNCTION obter_se_reserva_pa ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w   varchar(1);
nr_seq_interno_w unidade_atendimento.nr_seq_interno%type;


BEGIN

begin
	select nr_seq_interno
	into STRICT nr_seq_interno_w
	from unidade_atendimento
	where cd_setor_atendimento  = cd_setor_atendimento_p
	and  cd_unidade_basica  = cd_unidade_basica_p
	and  cd_unidade_compl  = cd_unidade_compl_p
	and ((cd_paciente_reserva IS NOT NULL AND cd_paciente_reserva::text <> '') or (nm_pac_reserva IS NOT NULL AND nm_pac_reserva::text <> ''));
exception
when others then
	nr_seq_interno_w := 0;
	ds_retorno_w  := 'N';
end;

if (coalesce(nr_seq_interno_w,0) > 0)  then
	begin
		select 'S'
		into STRICT ds_retorno_w
		from   gestao_vaga a,
		atendimento_paciente b
		where  b.ie_tipo_atendimento = 3
		and    a.nr_atendimento = b.nr_atendimento
		and    a.cd_setor_desejado = cd_setor_atendimento_p
		and    a.cd_unidade_basica = cd_unidade_basica_p
		and    a.cd_unidade_compl = cd_unidade_compl_p
		and    coalesce(a.dt_acomodacao::text, '') = ''
		and  coalesce(b.dt_alta::text, '') = ''  LIMIT 1;
		exception
	when others then
		ds_retorno_w := 'N';
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
