CREATE OR REPLACE FUNCTION obter_se_responsavel_atend ( cd_perfil_p bigint, cd_pessoa_fisica_p text, cd_convenio_p bigint, ie_tipo_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE




retorno_w  		varchar(1) := 'N';
nr_seq_conselho_w 	bigint;
nr_sequencia_w		bigint;
qt_existe_resp_atend_w	bigint;


BEGIN

select	coalesce(max(nr_seq_conselho),0)
into STRICT	nr_seq_conselho_w
from 	pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (nr_seq_conselho_w > 0) then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	regra_responsavel_atend
	where	coalesce(cd_perfil, cd_perfil_p)  = cd_perfil_p
	and	nr_seq_conselho              = nr_seq_conselho_w;

	if (nr_sequencia_w > 0) then

		select	count(*)
		into STRICT	qt_existe_resp_atend_w
		from 	regra_resp_atend_compl
		where	nr_seq_regra_resp = nr_sequencia_w;

		if (qt_existe_resp_atend_w > 0) and (cd_convenio_p > 0) and (ie_tipo_atendimento_p > 0) then

			select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT	retorno_w
			from 	regra_resp_atend_compl
			where	((cd_convenio = cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''))
			and	((ie_tipo_atendimento = ie_tipo_atendimento_p) or (coalesce(ie_tipo_atendimento::text, '') = ''));

		else
			retorno_w := 'S';
		end if;

	else
		retorno_w := 'N';
	end if;

	end;
end if;


return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
