CREATE OR REPLACE FUNCTION obter_se_resposta_correta ( ds_resposta_p text, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ie_resposta_correta_w	varchar(2)  := 'N';
ds_resposta_correta_w	varchar(4000);

BEGIN

select	substr(obter_desc_expressao(cd_exp_resposta,ds_resposta),1,255) ds_resposta
into STRICT	ds_resposta_correta_w
from	qua_doc_quest_resp
where	nr_seq_questionario = nr_sequencia_p
and	ie_correto = 'S';

if (ds_resposta_correta_w = ds_resposta_p ) then
	ie_resposta_correta_w:= 'S';
end if;

return	ie_resposta_correta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
