CREATE OR REPLACE FUNCTION obter_se_resp_diverg_inspecao ( nr_seq_divergencia_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w		varchar(1) := 'S';
nr_seq_inspecao_w	bigint;
nr_seq_tipo_w		bigint;
ie_autor_comprador_w	varchar(1);
cd_comprador_w		varchar(10);
qt_registro_w		bigint;


BEGIN 
 
select	nr_seq_inspecao, 
	nr_seq_tipo 
into STRICT	nr_seq_inspecao_w, 
	nr_seq_tipo_w 
from	inspecao_divergencia 
where	nr_sequencia = nr_seq_divergencia_p;
 
select	coalesce(max(ie_autor_comprador),'N') 
into STRICT	ie_autor_comprador_w 
from	inspecao_tipo_divergencia 
where	nr_sequencia = nr_seq_tipo_w;
 
if (ie_autor_comprador_w = 'S') then 
	begin 
 
	select	coalesce(max(a.cd_comprador),'0') 
	into STRICT	cd_comprador_w 
	from	ordem_compra a, 
		inspecao_recebimento b 
	where	b.nr_ordem_compra = a.nr_ordem_compra 
	and	b.nr_sequencia = nr_seq_inspecao_w;
 
	if (cd_comprador_w <> '0') and (cd_comprador_w <> cd_pessoa_fisica_p) then 
		ie_retorno_w	:= 'N';
	end if;	
 
	end;
elsif (ie_autor_comprador_w = 'C') then 
	begin 
 
	select	count(*) 
	into STRICT	qt_registro_w 
	from	comprador a 
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	a.ie_situacao = 'A';
 
	if (qt_registro_w = 0) then 
		ie_retorno_w	:= 'N';
	end if;	
 
	end;
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
