SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_resp_lib (ie_tipo_regulacao_p text, nr_Seq_resposta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
qt_itens_w	bigint;

BEGIN
ds_retorno_w	:= 'S';


select	count(*)
into STRICT	qt_itens_w
from 	REGRA_RESPOSTA_TIPO_REG
where	ie_tipo_regulacao = ie_tipo_regulacao_p;

if (qt_itens_w > 0) then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from 	REGRA_RESPOSTA_TIPO_REG
	where	ie_tipo_regulacao = ie_tipo_regulacao_p
	and	nr_Seq_resposta = nr_Seq_resposta_p;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
