CREATE OR REPLACE FUNCTION obter_se_resp_modulo_ap_soft ( nr_sequencia_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
qt_existe_w		bigint;


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) and (coalesce(cd_pessoa_fisica_p,'0') <> '0') then
	begin
	select	count(*)
	into STRICT	qt_existe_w
	from	aplicacao_soft_mod_resp
	where	nr_seq_modulo = nr_sequencia_p;

	if (qt_existe_w > 0) then
		begin
		select	'S'
		into STRICT	ie_retorno_w
		from	aplicacao_soft_mod_resp
		where	nr_seq_modulo = nr_sequencia_p
		and	cd_pessoa_fisica = cd_pessoa_fisica_p  LIMIT 1;
		exception
		when others then
			ie_retorno_w := 'N';
		end;
	end if;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
