SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_resp_pendencia (nr_seq_pendencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_resp_pend_w			varchar(1);
nr_seq_estagio_w		bigint;
nr_seq_regra_resp_w		bigint;

cd_pessoa_usuario_w		varchar(10);
cd_pessoa_fisica_w		varchar(10);
cd_setor_atendimento_w		integer;
cd_perfil_w			integer;
qt_setor_w			bigint;
qt_perfil_w			bigint;

C01 CURSOR FOR
	SELECT	cd_pessoa_fisica,
		cd_setor_atendimento,
		cd_perfil
	from	cta_regra_resp_item
	where 	nr_seq_regra = nr_seq_regra_resp_w
	and 	nr_seq_estagio = nr_seq_estagio_w
	order by coalesce(cd_pessoa_fisica,'0'),
		coalesce(cd_setor_atendimento,0),
		coalesce(cd_perfil,0);


BEGIN

ie_resp_pend_w:= 'N';

select 	nr_seq_regra_resp,
	nr_seq_estagio
into STRICT	nr_seq_regra_resp_w,
	nr_seq_estagio_w
from 	cta_pendencia
where 	nr_sequencia = nr_seq_pendencia_p;


open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w,
	cd_setor_atendimento_w,
	cd_perfil_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(cd_pessoa_fisica_w,'0') <> '0') then

		select 	coalesce(max(cd_pessoa_fisica),'0')
		into STRICT	cd_pessoa_usuario_w
		from 	usuario
		where 	nm_usuario = nm_usuario_p;

		if (cd_pessoa_fisica_w = cd_pessoa_usuario_w) then
			ie_resp_pend_w:= 'S';
		else
			ie_resp_pend_w:= 'N';
		end if;

	end if;

	if (coalesce(cd_setor_atendimento_w,0) <> 0) then

		select 	count(*)
		into STRICT	qt_setor_w
		from 	usuario_setor_v
		where 	nm_usuario = nm_usuario_p
		and 	cd_setor_atendimento = cd_setor_atendimento_w;

		if (qt_setor_w > 0) then
			ie_resp_pend_w:= 'S';
		else
			ie_resp_pend_w:= 'N';
		end if;

	end if;

	if (coalesce(cd_perfil_w,0) <> 0) then

		select 	count(*)
		into STRICT	qt_perfil_w
		from 	usuario_perfil
		where 	nm_usuario = nm_usuario_p
		and 	cd_perfil = cd_perfil_w;

		if (qt_perfil_w > 0) then
			ie_resp_pend_w:= 'S';
		else
			ie_resp_pend_w:= 'N';
		end if;

	end if;

	if (ie_resp_pend_w = 'S') then
		exit;
	end if;

	end;
end loop;
close C01;

return	ie_resp_pend_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
