CREATE OR REPLACE FUNCTION obter_se_resp_processo_aprov ( nr_sequencia_p bigint, cd_pessoa_fisica_p text, cd_cargo_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
qt_existe_w		smallint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	processo_aprov_compra a
where	a.nr_sequencia = nr_sequencia_p
and	(((obter_se_proc_por_nivel(a.nr_sequencia, cd_estabelecimento_p) = 'S') and (obter_se_proc_mesmo_nivel(a.nr_sequencia, a.nr_seq_proc_aprov, cd_estabelecimento_p, cd_pessoa_fisica_p) = 'S')) or
		   ((obter_se_proc_por_nivel(a.nr_sequencia, cd_estabelecimento_p) in ('N', 'A')) and
		   ((a.cd_pessoa_fisica = cd_pessoa_fisica_p) or (substr(obter_se_pessoa_delegacao(a.nr_sequencia,a.nr_seq_proc_aprov,cd_pessoa_fisica_p, 'AC', clock_timestamp()),1,1) = 'S') or (a.cd_cargo = cd_cargo_p) or
	((obter_dados_parametro_compras(cd_estabelecimento_p,25) = 'S') and (obter_se_cargo_mesmo_nivel(a.cd_cargo, cd_cargo_p, cd_estabelecimento_p) = 'S')) or
	((obter_dados_parametro_compras(cd_estabelecimento_p,25) = 'S') and (obter_se_pessoa_mesmo_nivel(a.cd_pessoa_fisica, cd_pessoa_fisica_p, cd_estabelecimento_p) = 'S'))
	)));

if (qt_existe_w > 0) then
	ie_retorno_w	:= 'S';
else
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
