CREATE OR REPLACE FUNCTION obter_se_restringe_div_ins_oc ( nr_seq_regra_p bigint, cd_cnpj_p text) RETURNS varchar AS $body$
DECLARE


ie_restringe_w		varchar(1);
qt_registro_w		bigint;



BEGIN

select	count(*)
into STRICT	qt_registro_w
from	regra_diverg_ins_oc_rest
where	nr_seq_regra = nr_seq_regra_p;

if (qt_registro_w = 0) then
	ie_restringe_w := 'S';
else
	ie_restringe_w	:= 'N';

	select	count(*)
	into STRICT	qt_registro_w
	from	regra_diverg_ins_oc_rest
	where	nr_seq_regra = nr_seq_regra_p
	and	cd_cnpj = cd_cnpj_p
	and	ie_restringe = 'S';

	if (qt_registro_w > 0) then
		ie_restringe_w := 'S';
	end if;

end if;

return	ie_restringe_w;

end;
