CREATE OR REPLACE FUNCTION obter_se_restringe_refeicao ( cd_refeicao_p text, ie_restringe_p text) RETURNS varchar AS $body$
DECLARE

ie_restringe_w	varchar(1);


BEGIN

if (cd_refeicao_p IS NOT NULL AND cd_refeicao_p::text <> '') and (ie_restringe_p IS NOT NULL AND ie_restringe_p::text <> '') then
	if (ie_restringe_p = 'N') then
		ie_restringe_w := 'S';
	elsif (ie_restringe_p = 'S') then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_restringe_w
		from	horario_refeicao_dieta
		where	cd_refeicao = cd_refeicao_p;
	end if;
end if;

return	ie_restringe_w;

end;
$body$
