SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_result_atend_lib (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_resultado_w		varchar(1) := 'N';
qt_result_laudo_w	bigint;



BEGIN


if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	/*Exames de imagem*/

	select	count(*)
	into STRICT	qt_result_laudo_w
	from	laudo_paciente l,
		procedimento_paciente p
	where	p.nr_sequencia 	 = l.nr_seq_proc
	and	p.nr_atendimento = nr_atendimento_p
	and	(l.dt_liberacao IS NOT NULL AND l.dt_liberacao::text <> '');

	if (qt_result_laudo_w <= 0) then

		/*Exames de laboratoriais*/

		select	count(*)
		into STRICT	qt_result_laudo_w
		from	prescr_procedimento r,
			prescr_medica	m
		where	m.nr_atendimento = nr_atendimento_p
		and	m.nr_prescricao	 = r.nr_prescricao
		and	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
		and	ie_status_atend >= 35;

	end if;


	if (qt_result_laudo_w > 0) then
		ie_resultado_w := 'S';
	else
		ie_resultado_w := 'N';
	end if;

end if;

return	ie_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_result_atend_lib (nr_atendimento_p bigint) FROM PUBLIC;

