CREATE OR REPLACE FUNCTION obter_se_retorno_conta_receb (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_situacao_w	varchar(1)	:= 'N';


BEGIN
if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_situacao_w
	from	convenio_retorno_item i,
		convenio_retorno r
	where	i.nr_interno_conta	= nr_interno_conta_p
	and	i.nr_seq_retorno	= r.nr_sequencia
	and	r.ie_status_retorno	= 'R';
end if;

RETURN	ie_situacao_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
