CREATE OR REPLACE FUNCTION obter_se_retorno_estornado (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);			
			

BEGIN

begin
	select	'S'
	into STRICT	ds_retorno_w
	from	convenio_retorno a
	where	nr_seq_ret_estorno	= nr_sequencia_p
	and (a.ie_tipo_glosa      <> 'A'
    or (a.ie_tipo_glosa        = 'A'
    and exists (SELECT 1 from convenio_retorno x where x.nr_seq_ret_adiant = a.nr_sequencia
      )))  LIMIT 1;
exception
when others then
	ds_retorno_w	:= 'N';
	
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
