SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_retorno_receb (nr_seq_retorno_p bigint, nr_seq_receb_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
qt_registro_w		bigint;
nr_seq_retorno_w	bigint;
nr_seq_ret_posterior_w	bigint;
qt_loop_w		bigint;

c01 CURSOR FOR
SELECT	nr_seq_retorno
from	convenio_ret_receb
where	nr_seq_receb	= nr_seq_receb_p;


BEGIN

if (nr_seq_retorno_p IS NOT NULL AND nr_seq_retorno_p::text <> '') and (nr_seq_receb_p IS NOT NULL AND nr_seq_receb_p::text <> '') then

	select	count(*)
	into STRICT	qt_registro_w
	from	convenio_ret_receb
	where	nr_seq_retorno	= nr_seq_retorno_p
	and	nr_seq_receb	= nr_seq_receb_p;

	if (qt_registro_w > 0) then
		ie_retorno_w	:= 'S';
	else
		open c01;
		loop
		fetch c01 into
			nr_seq_retorno_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			nr_seq_ret_posterior_w	:= 0;
			qt_loop_w	:= 0;

			while(nr_seq_ret_posterior_w IS NOT NULL AND nr_seq_ret_posterior_w::text <> '') and (ie_retorno_w = 'N') and (qt_loop_w < 6) loop
				select	max(nr_sequencia)
				into STRICT	nr_seq_ret_posterior_w
				from	convenio_retorno
				where	nr_seq_ret_origem	= nr_seq_retorno_w;

				if (nr_seq_ret_posterior_w = nr_seq_retorno_p) then
					ie_retorno_w	:= 'S';
				end if;

				qt_loop_w	:= qt_loop_w + 1;
			end loop;
		end loop;
		close c01;
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
