CREATE OR REPLACE FUNCTION obter_se_rev_est_classif_lib ( nr_seq_estagio_p bigint, nm_usuario_p text, cd_setor_usuario_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w	varchar(5) := 'S';
ie_regra_w	varchar(5);


BEGIN


begin
select	coalesce(max('S'),'N')
into STRICT	ie_regra_w
from	regra_rev_est_classif_lib a,
	estagio_revisao_proc_int b
where	b.nr_sequencia 	 = a.nr_seq_regra_rev_classif
and	b.nr_sequencia = nr_seq_estagio_p  LIMIT 1;
exception
when others then
	ie_regra_w := 'N';
end;


if (coalesce(ie_regra_w,'N') = 'S') then

	begin
	select	coalesce(max('S'),'N')
	into STRICT	ie_liberado_w
	from	regra_rev_est_classif_lib a,
		estagio_revisao_proc_int b
	where	b.nr_sequencia = a.nr_seq_regra_rev_classif
	and	b.nr_sequencia = nr_seq_estagio_p
	and	((a.nm_usuario_lib = nm_usuario_p) or (a.cd_setor_atendimento = cd_setor_usuario_p))  LIMIT 1;
	exception
	when others then
		ie_liberado_w := 'N';
	end;

end if;


return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
