CREATE OR REPLACE FUNCTION obter_se_rn_possui_alta ( nr_atendimento_p bigint, ie_tipo_alta_p text default 'M') RETURNS varchar AS $body$
DECLARE


dt_alta_medico_w		timestamp;
ie_possui_alta_w	varchar(1) := 'N';
dt_alta_w				timestamp;

BEGIN
/* ie_tipo_alta_p

A - Alta hospitalar
M - Alta mÃ©dica

*/
if (coalesce(nr_atendimento_p,0) > 0) then

		select	max(dt_alta_medico),
				max(dt_alta)
		into STRICT	dt_alta_medico_w,
				dt_alta_w
		from    atendimento_paciente
		where   nr_atendimento_mae = nr_atendimento_p;

			if (coalesce(ie_tipo_alta_p, 'M') = 'A') then
				if (dt_alta_w IS NOT NULL AND dt_alta_w::text <> '')	then
					ie_possui_alta_w := 'S';
				else
					ie_possui_alta_w := 'N';
				end if;
			else
				if (dt_alta_medico_w IS NOT NULL AND dt_alta_medico_w::text <> '')	then
					ie_possui_alta_w := 'S';
				else
					ie_possui_alta_w := 'N';
				end if;
			end if;
end if;

return	ie_possui_alta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
