CREATE OR REPLACE FUNCTION obter_se_rop_passou_vida_util ( nr_seq_roupa_p bigint) RETURNS varchar AS $body$
DECLARE


ie_atingiu_w		varchar(1) := 'N';
qt_vida_util_lav_w	integer;
qt_lavagens_w		bigint;

BEGIN

select	coalesce(a.qt_vida_util_lav,0),
	rop_obter_qtde_lavagens(b.nr_sequencia)
into STRICT	qt_vida_util_lav_w,
	qt_lavagens_w
from	rop_lote_roupa a,
	rop_roupa b
where	a.nr_sequencia = b.nr_seq_lote_roupa
and	b.nr_sequencia = nr_seq_roupa_p;

if (qt_lavagens_w >= qt_vida_util_lav_w) then
	ie_atingiu_w := 'S';
end if;

return	ie_atingiu_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
