CREATE OR REPLACE FUNCTION obter_se_rouparia_hotelaria ( nr_seq_unidade_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

SELECT	CASE WHEN COUNT(1)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
FROM	sl_unid_atend x,
		sl_servico y
WHERE	x.nr_seq_unidade	=  	nr_seq_unidade_p
AND		y.nr_sequencia		=	coalesce(x.nr_seq_servico_exec,x.nr_seq_servico)
and		coalesce(x.dt_fim::text, '') = ''
and		x.ie_status_serv <> 'C'
AND		y.IE_EXECUTOR IN ('O','R')  LIMIT 1;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_rouparia_hotelaria ( nr_seq_unidade_p bigint) FROM PUBLIC;

