CREATE OR REPLACE FUNCTION obter_se_roupa_passou_lavacao (nr_seq_roupa_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint;
ie_passou_lavacao_w	varchar(1) := 'S';
ds_tipo_local_w		varchar(15);

c01 CURSOR FOR
SELECT	substr(rop_obter_dados_local(a.nr_seq_local_orig_dest,'T'),1,15) ds_tipo_local
into STRICT	qt_registro_w
from	rop_lote_movto a,
	rop_movto_roupa b
where	a.nr_sequencia = b.nr_seq_lote
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and	substr(rop_obter_dados_operacao(a.nr_seq_operacao,'T'),1,15) in ('TS','EL')
and	b.nr_seq_roupa = nr_seq_roupa_p
order by	a.dt_liberacao desc,
	a.nr_sequencia;


BEGIN

/*Ver se existe alguma tranferencia para setores de utilização*/

select	count(*)
into STRICT	qt_registro_w
from	rop_lote_movto a,
	rop_movto_roupa b
where	a.nr_sequencia = b.nr_seq_lote
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and	substr(rop_obter_dados_operacao(a.nr_seq_operacao,'T'),1,15) in ('TS')
and	substr(rop_obter_dados_local(a.nr_seq_local_orig_dest,'T'),1,15) = 'S'
and	b.nr_seq_roupa = nr_seq_roupa_p;

if (qt_registro_w > 0) then

	open C01;
	loop
	fetch C01 into
		ds_tipo_local_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (ds_tipo_local_w in ('LI','LE')) then
			ie_passou_lavacao_w := 'S';
			exit;
		elsif (ds_tipo_local_w = 'S') then
			ie_passou_lavacao_w := 'N';
			exit;
		end if;
		end;
	end loop;
	close C01;
end if;

return	ie_passou_lavacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_roupa_passou_lavacao (nr_seq_roupa_p bigint) FROM PUBLIC;

