CREATE OR REPLACE FUNCTION obter_se_sae_evol_nao_lib (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(1) := 'N';
--evolucao_paciente_w	number(5); 
qt_pe_prescricao_w	integer;
	

BEGIN 
 
/* 
select	count(*) 
into	evolucao_paciente_w 
from	evolucao_paciente 
where	nm_usuario = nm_usuario_p 
and	dt_evolucao > sysdate - 30 
and	obter_se_atendimento_alta(nr_atendimento) = 'N' 
and	ie_evolucao_clinica not in ('AE','CO') 
and	dt_liberacao is null; 
*/
 
 
select	count(*) 
into STRICT	qt_pe_prescricao_w 
from	pe_prescricao 
where	nm_usuario = nm_usuario_p 
and	coalesce(dt_liberacao::text, '') = '' 
and	obter_se_atendimento_alta(nr_atendimento) = 'N' 
and	dt_prescricao > clock_timestamp() - interval '30 days';
 
if (qt_pe_prescricao_w > 0) then 
	ds_retorno_w := 'S';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
