SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_sae_vigente ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w	varchar(10)	:= 'S';				
qt_reg_w		bigint;
				

BEGIN 
 
if (ie_opcao_p	= 'C') then 
	select	count(1) 
	into STRICT	qt_reg_w 
	from  pe_prescricao x 
	where x.nr_atendimento = nr_atendimento_p 
	and   (x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '') 
	and   coalesce(x.dt_inativacao::text, '') = '' 
	and clock_timestamp() between x.DT_INICIO_PRESCR and x.DT_VALIDADE_PRESCR;
	 
	if (qt_reg_w	= 0) then 
		return 'N';
	end if;
elsif (ie_opcao_p	= 'S') then 
	select	count(1) 
	into STRICT	qt_reg_w 
	from  pe_prescricao x 
	where x.nr_atendimento = nr_atendimento_p 
	and   (x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '') 
	and   coalesce(x.dt_inativacao::text, '') = '' 
	and clock_timestamp() between x.DT_INICIO_PRESCR and x.DT_VALIDADE_PRESCR;
	 
	if (qt_reg_w	= 0) then 
		return 'S';
	else 
		return 'N';
	end if;
end if;
 
 
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_sae_vigente ( nr_atendimento_p bigint, ie_opcao_p text) FROM PUBLIC;

