CREATE OR REPLACE FUNCTION obter_se_saida_real ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_saida_real_w varchar(1);		
ie_alta_w varchar(1);
ie_dt_saida_real_w varchar(1);
ie_dt_alta_tesouraria_w varchar(1);


BEGIN	 
ie_saida_real_w := 'N';	
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	ie_saida_real_w := 'N';
	 
	ie_alta_w := obter_se_atendimento_alta(nr_atendimento_p);
 
	select	CASE WHEN coalesce(dt_saida_real::text, '') = '' THEN 'S'  ELSE 'N' END  
	into STRICT	ie_dt_saida_real_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
                      
	select	CASE WHEN coalesce(dt_alta_tesouraria::text, '') = '' THEN 'S'  ELSE 'N' END  
	into STRICT ie_dt_alta_tesouraria_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	if (ie_alta_w = 'S') and (ie_dt_saida_real_w = 'S') and (ie_dt_alta_tesouraria_w = 'S') then 
		begin 
		ie_saida_real_w := 'S';
		end;
	end if;
	end;
end if;
return ie_saida_real_w;
end;
