CREATE OR REPLACE FUNCTION obter_se_sala_cirur_disp ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_disponivel_w		varchar(1) := 'S';
nr_atendimento_w	bigint;
ds_classificacoes_w		varchar(255) := '';


BEGIN
if (obter_funcao_ativa = 3111) then
	ds_classificacoes_w := Obter_param_Usuario(3111, 306, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ds_classificacoes_w);
end if;

select	coalesce(max('N'),'S'),
	coalesce(max(nr_atendimento),0)
into STRICT	ie_disponivel_w,
	nr_atendimento_w
from	atend_paciente_unidade
where	cd_setor_atendimento	=	cd_setor_atendimento_p
and	cd_unidade_basica	=	cd_unidade_basica_p
and	cd_unidade_compl	=	cd_unidade_compl_p
and	((coalesce(ds_classificacoes_w,'XPTO') = 'XPTO') or (obter_se_contido_entre_virgula(ds_classificacoes_w, obter_classif_setor(cd_setor_atendimento)) = 'N'))
and	dt_entrada_unidade > clock_timestamp() - interval '3 days'
and	coalesce(dt_saida_unidade::text, '') = '';

if (ie_disponivel_w = 'N') and (nr_atendimento_w > 0) and (nr_atendimento_w = nr_atendimento_p) then
	ie_disponivel_w:= 'S';
end if;

return ie_disponivel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
