CREATE OR REPLACE FUNCTION obter_se_sala_pac_cirur ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
nr_cirurgia_w		bigint;


BEGIN

select	coalesce(max(a.nr_cirurgia),0)
into STRICT	nr_cirurgia_w
from	atend_paciente_unidade b,
	cirurgia a
where	a.nr_atendimento = b.nr_atendimento
and	a.dt_entrada_unidade = b.dt_entrada_unidade
and	b.cd_setor_atendimento = cd_setor_atendimento_p
and	b.cd_unidade_basica = cd_unidade_basica_p
and	b.cd_unidade_compl = cd_unidade_compl_p
and	b.dt_entrada_unidade > clock_timestamp() - interval '3 days'
and	coalesce(b.dt_saida_unidade::text, '') = '';


if (nr_cirurgia_w > 0)	then
	select	coalesce(max(a.cd_pessoa_fisica),'')
	into STRICT	cd_pessoa_fisica_w
	from	cirurgia a
	where	a.nr_cirurgia = nr_cirurgia_w;
end if;

if (cd_pessoa_fisica_p = cd_pessoa_fisica_w) then
	return	'S';
else
	return	'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
