CREATE OR REPLACE FUNCTION obter_se_saldo_caixa_aberto (nr_seq_caixa_p bigint, nr_seq_saldo_p bigint, dt_saldo_p timestamp, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);


BEGIN

if (nr_seq_saldo_p IS NOT NULL AND nr_seq_saldo_p::text <> '') then
	select CASE WHEN qt_saldo_aberto=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from (	SELECT 	count(*) qt_saldo_aberto
			from 	caixa_saldo_diario
			where 	coalesce(dt_fechamento::text, '') = ''
			and 	nr_sequencia = nr_seq_saldo_p) alias3;
else
	select CASE WHEN qt_saldo_aberto=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from (	SELECT 	count(*) qt_saldo_aberto
			from 	caixa_saldo_diario
			where 	coalesce(dt_fechamento::text, '') = ''
			and nr_seq_caixa = nr_seq_caixa_p
			and	dt_saldo = dt_saldo_p) alias2;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
