CREATE OR REPLACE FUNCTION obter_se_saldo_estrang (nr_seq_caixa_saldo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_saldo_estrang_w	varchar(1) := 'N';
cont_w			bigint;


BEGIN
	select	count(*)
	into STRICT	cont_w
	from 	caixa_saldo_estrang
	where	nr_seq_caixa_saldo = nr_seq_caixa_saldo_p;
	
	if (coalesce(cont_w,0) > 0) then
		ie_saldo_estrang_w := 'S';
	else
		ie_saldo_estrang_w := 'N';
	end if;
	
	return ie_saldo_estrang_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
