SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_schematic_conf ( nr_sequencia_p bigint, ie_opcao_p text default null) RETURNS varchar AS $body$
DECLARE

	
ie_configuravel_w	objeto_schematic.ie_configuravel%type;	
vl_parametro_padrao_w	objeto_schematic_param.vl_parametro_padrao%type;

/*	ie_opcao_p
	P - Identifica se o objeto pode ser visualizado apenas nas bases da Philips
*/
BEGIN

select	max(ie_configuravel)
into STRICT	ie_configuravel_w
from	objeto_schematic
where	nr_sequencia = nr_sequencia_p;

if (ie_configuravel_w = 'S') then
	select 	max(a.vl_parametro_padrao)
	into STRICT	vl_parametro_padrao_w
	from 	objeto_schematic_param a
	where 	a.nr_seq_obj_sch = nr_sequencia_p;

	if (vl_parametro_padrao_w = 'C') and (obter_se_base_corp = 'N') and (obter_se_base_wheb = 'N') then --Se somente CORP/WHEB, nao permite configuracao do usuario.
		if (ie_opcao_p = 'P') then
			ie_configuravel_w := 'P';
		else
			ie_configuravel_w := 'N';
		end if;
	elsif (vl_parametro_padrao_w = 'I') then
			ie_configuravel_w := 'I';
	end if;
end if;

return	ie_configuravel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
