CREATE OR REPLACE FUNCTION obter_se_score_flexii_lib_suep (nr_seq_escala_p bigint) RETURNS varchar AS $body$
DECLARE


ie_apresenta_escala_w	varchar(5);
qt_lib_w				bigint;
cd_perfil_w				bigint;


BEGIN

cd_perfil_w	:= obter_perfil_ativo;

select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ie_apresenta_escala_w
from	eif_escala_ii_lib
where	nr_seq_escala	 =  nr_seq_escala_p;

if (ie_apresenta_escala_w = 'N') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_apresenta_escala_w
	from	eif_escala_ii_lib
	where	coalesce(nr_seq_escala,nr_seq_escala_p) 	= nr_seq_escala_p
	and		coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w;
	
end if;

return	ie_apresenta_escala_w;

end;
$body$
