CREATE OR REPLACE FUNCTION obter_se_sc_item_sem_depara ( nr_solic_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_registro_w		bigint;			
ie_retorno_w		varchar(1) := 'S';
			

BEGIN 
 
select	count(*) 
into STRICT	qt_registro_w 
from	solic_compra a, 
	solic_compra_item b 
where	a.nr_solic_compra = b.nr_solic_compra 
and	a.nr_solic_compra = nr_solic_compra_p 
and	obter_se_regra_int_vigente(b.cd_material,'ME',a.cd_estabelecimento) = 'N';
 
if (qt_registro_w > 0) then 
	ie_retorno_w := 'N';
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
