CREATE OR REPLACE FUNCTION obter_se_segurado_pf ( cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	pls_segurado	b,
	pessoa_fisica	a
where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
and	coalesce(b.dt_rescisao::text, '') = ''
and	b.ie_tipo_segurado = 'B'
and	(b.dt_contratacao IS NOT NULL AND b.dt_contratacao::text <> '')
and	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

return	qt_registros_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
