CREATE OR REPLACE FUNCTION obter_se_senha_outro_pf ( cd_pessoa_fisica_p text, ds_senha_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(1) := 'N';
dt_nasc_w	timestamp;

BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	 
	select 	max(dt_nascimento) 
	into STRICT	dt_nasc_w 
	from	pessoa_fisica 
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p;
	 
	begin 
		select	'S' 
		into STRICT	ds_retorno_w	 
		from 	pessoa_fisica a, 
			atendimento_paciente b 
		where 	a.cd_pessoa_fisica <> cd_pessoa_fisica_p 
		and	a.cd_pessoa_fisica = b.cd_pessoa_fisica  
		and	trunc(a.dt_nascimento) = trunc(coalesce(dt_nasc_w,clock_timestamp() + interval '1 days')) 
		and	b.ds_senha = ds_senha_p 
		and	(b.ds_senha IS NOT NULL AND b.ds_senha::text <> '') LIMIT 1;
	exception 
	when others then 
		ds_retorno_w := 'N';
	end;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
