CREATE OR REPLACE FUNCTION obter_se_sepse_liberada (nr_atendimento_p bigint, ie_opcao_p text default 'L') RETURNS varchar AS $body$
DECLARE


retorno_w 					varchar(1) := 'A';
qt_reg_w  					smallint;
cd_estabelecimento_w        smallint;
cd_setoratendimento_w       integer;
qt_idade_paciente_w         bigint;
qt_idade_dias_w             bigint;
qt_peso_gramas_w            double precision;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from    sepse_regra_liberacao;

if (qt_reg_w > 0 ) then

	select	max((obter_idade_pf(obter_pessoa_atendimento(nr_atendimento_p, 'C'), clock_timestamp(), 'A'))::numeric ),
		obter_setor_atendimento(nr_atendimento_p),
		obter_dados_atendimento(nr_atendimento_p,'EST'),
		trunc(max((obter_idade_pf(obter_pessoa_atendimento(nr_atendimento_p, 'C'), clock_timestamp(), 'DIA'))::numeric )),
		coalesce(obter_peso_pf(obter_pessoa_atendimento(nr_atendimento_p, 'C')),0) * 1000
	into STRICT	qt_idade_paciente_w,
		cd_setoratendimento_w,
		cd_estabelecimento_w,
		qt_idade_dias_w,
		qt_peso_gramas_w
	;
	
	if (ie_opcao_p = 'F') then
	
		select	coalesce(max(coalesce(ie_finaliza_manual,'N')),'N')
		into STRICT	retorno_w
		from    sepse_regra_liberacao
		where (cd_estabelecimento   = cd_estabelecimento_w  or coalesce(cd_estabelecimento::text, '') = '')
		and (cd_setor_atendimento = cd_setoratendimento_w or coalesce(cd_setor_atendimento::text, '') = '')
		and     coalesce(qt_idade_paciente_w,0) between coalesce(qt_idade_min,0) and coalesce(qt_idade_max, 200)
		and     coalesce(qt_idade_dias_w,0) between coalesce(qt_dias_min,0) and coalesce(qt_dias_max,99999)
		and	   qt_peso_gramas_w between coalesce(qt_peso_gramas_min,0) and coalesce(qt_peso_gramas_max,999999999);
	
	else
	
		select	coalesce(max(coalesce(ie_tipo_sepse,'A')),'N')
		into STRICT	retorno_w
		from    sepse_regra_liberacao
		where (cd_estabelecimento   = cd_estabelecimento_w  or coalesce(cd_estabelecimento::text, '') = '')
		and (cd_setor_atendimento = cd_setoratendimento_w or coalesce(cd_setor_atendimento::text, '') = '')
		and     coalesce(qt_idade_paciente_w,0) between coalesce(qt_idade_min,0) and coalesce(qt_idade_max, 200)
		and     coalesce(qt_idade_dias_w,0) between coalesce(qt_dias_min,0) and coalesce(qt_dias_max,99999)
		and	qt_peso_gramas_w between coalesce(qt_peso_gramas_min,0) and coalesce(qt_peso_gramas_max,999999999);
	
	end if;
	
end if;	

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
