CREATE OR REPLACE FUNCTION obter_se_seq_etapa_maior (nr_interno_conta_p bigint, nr_seq_etapa_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE




nr_seq_etapa_w		bigint;
nr_seq_etapa_filtro_w	bigint;
ds_retorno_w		varchar(1);


BEGIN

ds_retorno_w := 'N';

nr_seq_etapa_filtro_w:= nr_seq_etapa_p;

select	max(b.nr_seq_etapa)
into STRICT	nr_seq_etapa_w
from	fatur_etapa		b,
  	conta_paciente_etapa	a
where	a.nr_seq_etapa		= b.nr_sequencia
and	a.nr_interno_conta	= nr_interno_conta_p
and 	b.cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(b.ie_situacao,'A')	= 'A';

if (nr_seq_etapa_w = nr_seq_etapa_filtro_w) then
	ds_retorno_w := 'S';
end if;

return ds_retorno_w;

end;
