SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_serv_lib_perfil ( nr_sequencia_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_cd_perfil_w	bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	-- VERIFICA SE HÁ ALGUMA REGRA DE PERFIL PARA O SERVIÇO. SE NÃO HOUVER NENHUMA, LIBERAR O SERVIÇO PARA TODOS OS PERFIS.
	select 	count(*)
	into STRICT	qt_cd_perfil_w
	from	sl_servico_regra_perfil
	where	nr_seq_servico = nr_sequencia_p
	and		coalesce(ie_situacao,'I') = 'A';

	if (qt_cd_perfil_w = 0) then
		ds_retorno_w := 'S';
	else
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT 	ds_retorno_w
		from	sl_servico_regra_perfil
		where	nr_seq_servico = nr_sequencia_p
		and		cd_perfil = cd_perfil_p
		and		coalesce(ie_situacao,'I') = 'A';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
