CREATE OR REPLACE FUNCTION obter_se_setor_lib_ci ( nr_sequencia_p bigint, cd_setor_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w	char(1) := 'S';


BEGIN

select	coalesce(max('N'),'S')
into STRICT	ie_liberado_w
from	rep_regra_envio_ci_setor
where	nr_seq_regra	= nr_sequencia_p;

if (ie_liberado_w = 'N') then
	select	coalesce(max('S'), 'N')
	into STRICT	ie_liberado_w
	from	rep_regra_envio_ci_setor where		nr_seq_regra		= nr_sequencia_p
	and		cd_setor_atendimento	= cd_setor_p LIMIT 1;
end if;

return ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
