CREATE OR REPLACE FUNCTION obter_se_setor_lib_conv ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, ie_tipo_atendimento_p bigint, cd_setor_atendimento_p bigint ) RETURNS varchar AS $body$
DECLARE

qt_registro_w	bigint;

ie_bloqueia_atendimento_w	varchar(2)	:= 'L';

C01 CURSOR FOR
	SELECT	ie_libera_bloqueia
	from	conv_regra_atend_setor
	where	cd_estabelecimento					= cd_estabelecimento_p
	and	cd_convenio						= cd_convenio_p
	and	ie_situacao						= 'A'
	and	coalesce(cd_categoria,cd_categoria_p)			= cd_categoria_p
	and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_p)		= ie_tipo_atendimento_p
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_p)	= cd_setor_atendimento_p
	order by coalesce(ie_tipo_atendimento, 0),
		coalesce(cd_setor_atendimento, 0),
		coalesce(cd_categoria,'0');


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	conv_regra_atend_setor
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_convenio		= cd_convenio_p
and	ie_situacao		= 'A';

if (qt_registro_w		> 0) then
	open C01;
	loop
	fetch C01 into
		ie_bloqueia_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
end if;

return	coalesce(ie_bloqueia_atendimento_w,'L');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
