CREATE OR REPLACE FUNCTION obter_se_setor_lib_mot_exc_con ( cd_motivo_exc_conta_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_setor_w		bigint;
qt_setor_motivo_w	bigint;
ds_retorno_w		varchar(1) := 'N';
cd_setor_atendimento_w	bigint;
cd_perfil_w		integer;


BEGIN

cd_setor_atendimento_w	:= coalesce(cd_setor_atendimento_p,0);
cd_perfil_w		:= coalesce(obter_perfil_ativo,0);

select	count(*)
into STRICT	qt_setor_w
from	setor_lib_mot_exc_conta
where	cd_motivo_exc_conta = cd_motivo_exc_conta_p;

if (qt_setor_w > 0) then
	select	count(*)
	into STRICT	qt_setor_motivo_w
	from	setor_lib_mot_exc_conta
	where	cd_motivo_exc_conta = cd_motivo_exc_conta_p
	and	coalesce(cd_setor_atendimento, cd_setor_atendimento_w) = cd_setor_atendimento_w
	and 	coalesce(cd_perfil, cd_perfil_w) = cd_perfil_w;

	if (qt_setor_motivo_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
else
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
