CREATE OR REPLACE FUNCTION obter_se_setor_lib_pessoa ( cd_pessoa_fisica_p text, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
nm_usuario_w		varchar(15);
qt_existe_w		bigint;


BEGIN

select	coalesce(max(nm_usuario),'X')
into STRICT	nm_usuario_w
from	usuario
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (nm_usuario_w <> 'X') then
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	usuario_setor_v
	where	nm_usuario = nm_usuario_w
	and	cd_setor_atendimento = cd_setor_atendimento_p;

	if (qt_existe_w > 0) then
		ie_retorno_w	:= 'S';
	end if;

	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
