CREATE OR REPLACE FUNCTION obter_se_setor_ordem ( ie_acm_p text, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_gera_ordem_w		varchar(1) := 'S';
qt_cont_w		bigint := 0;


BEGIN

