CREATE OR REPLACE FUNCTION obter_se_setor_regra_atend ( cd_setor_atendimento_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_tipo_atendimento_w	ATENDIMENTO_PACIENTE.ie_tipo_atendimento%type;
cd_regra_w		varchar(10);
cd_convenio_w		CONVENIO.cd_convenio%type;
cd_categoria_w		CATEGORIA_CONVENIO.cd_categoria%type;
	

BEGIN 
 
If (nr_atendimento_p = 0) then 
	return 'S';
else 
	ie_tipo_atendimento_w := obter_tipo_atendimento(nr_atendimento_p);
	cd_convenio_w := obter_convenio_atendimento(nr_atendimento_p);
	cd_categoria_w := obter_categoria_atendimento(nr_atendimento_p);
	 
	-- Se estiver na EUP, pega o parâmetro 37. Se não, pega o 29 da Movimentação de Pacientes 
	if (obter_funcao_ativa = 916) then 
		cd_regra_w := obter_param_usuario(916, 37, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, cd_regra_w);
	else	 
		cd_regra_w := obter_param_usuario(3111, 29, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, cd_regra_w);
	end if;
	 
	return	obter_se_setor_regra_entrada(cd_setor_atendimento_p, 0, ie_tipo_atendimento_w, cd_regra_w, 0, cd_convenio_w, cd_categoria_w, obter_usuario_ativo);
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
