CREATE OR REPLACE FUNCTION obter_se_setor_triagem (cd_setor_atendimento_p bigint, ie_status_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(5)	:= 'S';
ds_status_w		varchar(10);

C01 CURSOR FOR
	SELECT	ds_status
	from	regra_setor_status_pa
	where	cd_setor_atendimento = cd_setor_atendimento_p
	order by cd_setor_atendimento;


BEGIN

open C01;
loop
fetch C01 into
	ds_status_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_retorno_w := obter_se_contido_char(ie_status_p,ds_status_w);

	if (ds_retorno_w	= 'S') then
		exit;
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
