CREATE OR REPLACE FUNCTION obter_se_setor_tubo ( cd_tubo_p regra_tubo_pneumatico.cd_barras_tubo%type, cd_setor_atend_p regra_tubo_pneumatico.cd_setor_atendimento%type) RETURNS bigint AS $body$
DECLARE


cd_setor_atendimento_w		regra_tubo_pneumatico.cd_setor_atendimento%type;

BEGIN

select 	coalesce(max(cd_setor_atendimento),0)
into STRICT	cd_setor_atendimento_w
from 	regra_tubo_pneumatico
where 	cd_barras_tubo = cd_tubo_p
and 	cd_setor_atendimento = cd_setor_atend_p
and 	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

if (cd_setor_atendimento_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1208956);
end if;

return cd_setor_atendimento_w;

end;
$body$
