CREATE OR REPLACE FUNCTION obter_se_setor_usuario ( cd_setor_atendimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_setor_usuario_w	varchar(1);


BEGIN

	begin
	select	'S'
	into STRICT	ie_setor_usuario_w
	from	usuario
	where	cd_setor_atendimento	= cd_setor_atendimento_p
	and	nm_usuario = nm_usuario_p  LIMIT 1;
	exception
	when no_data_found then
		ie_setor_usuario_w := 'N';
	end;


if (ie_setor_usuario_w = 'N') then
	
	begin
	select	'S'
	into STRICT	ie_setor_usuario_w
	from	usuario_setor
	where	cd_setor_atendimento	= cd_setor_atendimento_p
	and	nm_usuario_param = nm_usuario_p  LIMIT 1;
	exception
	when no_data_found then
		ie_setor_usuario_w := 'N';
	end;

end if;
	
return	ie_setor_usuario_w;


End;
