CREATE OR REPLACE FUNCTION obter_se_setor_vinculado_proc ( ie_origem_proced_p bigint, cd_procedimento_p bigint, cd_setor_atendimento_p bigint ) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(1) := 'N';
qt_setor_w	bigint;


BEGIN

if (coalesce(cd_procedimento_p,0) = 0) and (coalesce(ie_origem_proced_p,0) = 0) then
	ds_retorno_w := 'S';
else
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	procedimento
	where	cd_procedimento  	= cd_procedimento_p
	and	ie_origem_proced 	= ie_origem_proced_p
	and	cd_setor_exclusivo 	= cd_setor_atendimento_p;

	if (ds_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	procedimento_setor_atend
		where	cd_procedimento  	= cd_procedimento_p
		and	ie_origem_proced 	= ie_origem_proced_p
		and	cd_setor_atendimento 	= cd_setor_atendimento_p;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
